mapper.select:
select a.appointment_id,a.appointment_title,a.sponsor_date,u.avatar , af.follow_count , u.full_name as person from appointment a join user u on a.sponsor_id = u.user_id
left join (select appointment_id, count(*) follow_count from appointment_follower where follower_type='follower' group by appointment_id) af on a.appointment_id = af.appointment_id
where a.appointment_title like '%${keyword}%' and a.company_id=#{companyid} and a.indicator=1 limit #{skip} ,#{take}

SELECT
count(a.appointment_id)
FROM
appointment a
WHERE
a.company_id = #{companyId}
AND a.indicator = 1
 AND a.appointment_title LIKE '%${title}%'
 AND a.sponsor_date >= #{startDate}
  AND  a.sponsor_date <= #{endDate}

select ai.item_id,ai.content item_title,u.full_name sponsor_name, ai.sponsor_date, case when t.count is not null then t.count else 0 end as followerCount from appointment_item ai left join user u on ai.sponsor_id = u.user_id
left join ( select item_id , count(*) as count from appointment_follower where follower_type = 'follower' or follower_type = 'item_sponsor' group by item_id )t on ai.item_id= t.item_id
where appointment_id = #{appointmentId}

        select  a.appointment_id ,  a.appointment_title , c.company_name,  b.business_id,  b.business_name, u.full_name as sponsor_idfull_name,
u.cell_phone as sponsor_idcell_phone ,  i.contact_person, i.phone_number,  a.sponsor_date  from  appointment a
join company  c on a.company_id = c.company_id
join   company_info i on c.company_id = i.company_id
join   business   b on c.business_id=  b.business_id
join user u  on  a.sponsor_id = u.user_id

 where   a.indicator=1
         AND c.company_name LIKE '%${company_name}%'
         AND a.appointment_title LIKE '%${appointment_title}%'
          AND  b.business_id  = #{business_id}

        and a.sponsor_date  > #{start}
        and a.sponsor_date < #{end}
  LIMIT #{skip}, #{take}

select count(*) from appointment a join company c on a.company_id = c.company_id join company_info i on c.company_id = i.company_id join business b on c.business_id= b.business_id join user u on a.sponsor_id = u.user_id
where a.indicator=1 AND c.company_name LIKE '%${company_name}%' AND a.appointment_title LIKE '%${appointment_title}%' AND b.business_id = #{business_id}
and a.sponsor_date > #{start} and a.sponsor_date < #{end}
